import React from 'react';
import { SafeAreaView, StyleSheet, Text, View, Image, TextInput,KeyboardAvoidingView,Platform } from 'react-native';
import SubmitButton from '../../components/general/submitButton'
import AppLoading from 'expo-app-loading';
import {useFonts, PlayfairDisplay_700Bold,PlayfairDisplay_400Regular,PlayfairDisplay_800ExtraBold, PlayfairDisplay_700Bold_Italic, PlayfairDisplay_500Medium } from "@expo-google-fonts/playfair-display"
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

export default function Login({ navigation }) {
    let [fontsLoaded,err] = useFonts({
      PlayfairDisplay_700Bold,
      PlayfairDisplay_400Regular,
      PlayfairDisplay_800ExtraBold,
      PlayfairDisplay_700Bold_Italic,
      PlayfairDisplay_500Medium
    })

    if(!fontsLoaded){
      return <AppLoading/>
    }

    const navigateSignUp = () => {
      navigation.navigate("SignUp");
    }

    return (
      <SafeAreaView style={styles.container}>
        <View style={styles.logoContainer}>
          <Image 
            style={styles.logo} 
            source={require('../../../assets/favicon.png')}
          />
        </View>
        <View style={styles.welcome}>
          <Text style={styles.mainTitle}>
            HELLO
          </Text>
          <Text style={styles.subTitle}>
            welcome back!
          </Text>
          <View
            style={styles.line}
          />
          <Text style={styles.loginTitle}>
            LOGIN
          </Text>
        </View>
        <View style={styles.form}>
          <TextInput style={styles.box} placeholder='Type Your Username'>

          </TextInput>
          <TextInput style={styles.box} placeholder='Type Your Password'>

          </TextInput>
          <Text style={styles.forgot}>
            Forgot Your Password? 
            </Text>
        </View>
        <View style={styles.submitButton}>
          <SubmitButton text="LOGIN" navigateFunction={navigateSignUp}/>
          <Text style={styles.or}>
            or
          </Text>
          <Text style={styles.signup}>
            Sign up to Petriam
          </Text>
        </View>
        <View style={styles.footer}>
          <Text style={styles.footerText}>
            Petriam 2021 All Rights Reserved
          </Text>
        </View>
      </SafeAreaView>
    );
  }

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#F2F2F2',
    },
    logoContainer: {
      flex: 2,
      justifyContent: 'center'
    },
    logo: {
      width: 50,
      height: 50,
      alignSelf: 'center'
    },
    welcome: {
      flex: 2,
      justifyContent: 'center',
      paddingHorizontal: 10,
    },
    mainTitle:{
      fontFamily: "PlayfairDisplay_800ExtraBold",
      fontSize: 75,
      color: "#707070"
    },
    subTitle:{
      fontFamily: "PlayfairDisplay_700Bold_Italic",
      fontSize: 25,
      color: "#707070"
    },
    loginTitle:{
      fontFamily: "PlayfairDisplay_700Bold",
      fontSize: 25,
      color: "#707070"
    },
    line: {
      borderBottomColor: '#707070',
      borderBottomWidth: 1,
    },
    form: {
      flex: 2,
      justifyContent: 'center',
      alignItems: 'center',
    },
    box: {
      backgroundColor: 'white',
      borderRadius: 18,
      width: 369,
      height: 43,
      alignSelf: 'center',
      marginTop: 10
    },
    forgot: {
      alignSelf: 'flex-end',
      marginRight: 12,
      marginTop: 10,
      textDecorationLine: 'underline',
      fontFamily:"PlayfairDisplay_500Medium",
      color:"#707070",
      fontSize:10,
    },
    submitButton: {
      flex: 2,
      alignItems: 'center'
    },
    or: {
      marginVertical: 20,
      fontFamily: "PlayfairDisplay_400Regular",
      fontSize:10
    },
    signup: {
      textDecorationLine: 'underline',
      fontFamily: "PlayfairDisplay_700Bold",
      fontSize:15,
      color:"black"
    },
    footer: {
      flex: 1,
      justifyContent: 'flex-end',
    },
    footerText: {
      alignSelf: 'center',
      marginBottom: 10,
      fontFamily: "PlayfairDisplay_700Bold",
      fontSize:10,
      color:"#707070"
    }
  });
